(function($) {
  function cssWhenSlideActive() {
    var elJq = $('rs-module.js-overlay');
  	elJq.each(function() {
    	const el = this;
        if (!el) {
          setTimeout(cssWhenSlideActive, 500);
          return;
        }

        var observer = new MutationObserver(function(mutations) {
          mutations.forEach(function(mutation) {
            if (mutation.type === "attributes") {
              var key = mutation.target.getAttribute('data-slideactive');
              setTimeout(function() {
                $(el).find('rs-sbg-px').removeAttr('style');
                $('[data-key="'+key+'"]').find('rs-sbg-px').css('opacity', 1);
              }, 500);
            }
          });
        });

        observer.observe(el, {
          attributes: true //configure it to listen to attribute changes
        });
    });
  }
  cssWhenSlideActive();
})(jQuery);
